import React, { useState } from 'react';
import axios from 'axios';
import styles from './Upload.module.css';

function Upload() {
  const [photo, setPhoto] = useState(null);
  const [folder, setFolder] = useState('');
  const [message, setMessage] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage('');
    setError('');
    if (!photo || !folder) {
      setError('Vui lòng chọn ảnh và nhập tên folder');
      return;
    }
    const formData = new FormData();
    for (let i = 0; i < photo.length; i++) {
      formData.append('photos', photo[i]);
    }
    formData.append('folder', folder);
    try {
      await axios.post('http://localhost:5000/api/upload', formData, {
        withCredentials: true,
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      setMessage('Upload thành công!');
      setPhoto(null);
      setFolder('');
    } catch (err) {
      setError('Upload thất bại');
    }
  };

  return (
    <div className={styles.uploadContainer}>
      <div className={styles.uploadTitle}>Upload Ảnh</div>
      <form className={styles.uploadForm} onSubmit={handleSubmit}>
        <input
          type="file"
          accept="image/*"
          multiple
          className={styles.inputFile}
          onChange={e => setPhoto(e.target.files)}
        />
        <input
          className={styles.inputText}
          placeholder="Tên folder"
          value={folder}
          onChange={e => setFolder(e.target.value)}
        />
        <button className={styles.uploadButton} type="submit">Upload</button>
      </form>
      {message && <div className={styles.uploadMessage}>{message}</div>}
      {error && <div className={styles.uploadError}>{error}</div>}
    </div>
  );
}

export default Upload;
