import React, { useEffect, useState } from 'react';
import axios from 'axios';
import styles from './Gallery.module.css';

function Gallery() {
  const [folders, setFolders] = useState([]);
  const [photos, setPhotos] = useState([]);
  const [selectedFolder, setSelectedFolder] = useState(null);

  useEffect(() => {
    axios.get('http://localhost:5000/api/folders', {
      withCredentials: true
    }).then(res => setFolders(res.data));
  }, []);

  const handleFolderClick = (folder) => {
    setSelectedFolder(folder.name);
    axios.get(`http://localhost:5000/api/photos/${folder.name}`, {
      withCredentials: true
    }).then(res => setPhotos(res.data));
  };

  const handleBack = () => {
    setSelectedFolder(null);
    setPhotos([]);
  };

  return (
    <div className={styles.galleryContainer}>
      <h2>Th∆∞ m·ª•c ·∫£nh</h2>
      {!selectedFolder ? (
        <div className={styles.folderGrid}>
          {folders.map(folder => (
            <div
              key={folder.id}
              className={styles.folderCard}
              onClick={() => handleFolderClick(folder)}
            >
              <div className={styles.folderIcon}>üìÅ</div>
              <div className={styles.folderName}>{folder.name}</div>
            </div>
          ))}
        </div>
      ) : (
        <div>
          <button className={styles.backButton} onClick={handleBack}>‚Üê Quay l·∫°i</button>
          <h3 className={styles.folderTitle}>·∫¢nh trong folder: {selectedFolder}</h3>
          <div className={styles.photoGrid}>
            {photos.map(photo => (
              <div className={styles.photoItem} key={photo.id}>
                <img src={`http://localhost:5000/uploads/${selectedFolder}/${photo.filename}`} alt="" />
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
}

export default Gallery;
