import React, { useState } from 'react';
import axios from 'axios';

function Upload({ token }) {
  const [photos, setPhotos] = useState([]);
  const [folder, setFolder] = useState('');
  const [message, setMessage] = useState('');

  const handleFileChange = (e) => {
    const files = Array.from(e.target.files);
    setPhotos(prev => [...prev, ...files]);
    e.target.value = null;
  };

  const handleRemovePhoto = (index) => {
    setPhotos(prev => prev.filter((_, i) => i !== index));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (photos.length === 0 || !folder) {
      setMessage('Vui lòng chọn ảnh và nhập tên folder');
      return;
    }
    const formData = new FormData();
    photos.forEach(photo => {
      formData.append('photos', photo);
    });
    formData.append('folder', folder);
    try {
      await axios.post('http://localhost:5000/api/upload', formData, {
        withCredentials: true,
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      setMessage('Upload thành công!');
      setPhotos([]);
    } catch (err) {
      setMessage('Upload thất bại');
    }
  };

  return (
    <div>
      <h2>Upload Ảnh</h2>
      <form onSubmit={handleSubmit}>
        <input type="file" accept="image/*" multiple onChange={handleFileChange} />
        <input placeholder="Tên folder" value={folder} onChange={e => setFolder(e.target.value)} />
        <button type="submit">Upload</button>
      </form>
      {photos.length > 0 && (
        <ul style={{ listStyle: 'none', padding: 0 }}>
          {photos.map((photo, idx) => (
            <li key={idx} style={{ display: 'flex', alignItems: 'center', marginBottom: 4 }}>
              <span>{photo.name}</span>
              <button type="button" onClick={() => handleRemovePhoto(idx)} style={{ marginLeft: 8 }}>X</button>
            </li>
          ))}
        </ul>
      )}
      {message && <div>{message}</div>}
    </div>
  );
}

export default Upload;
