import React, { useEffect, useState } from 'react';
import axios from 'axios';
import styles from './Gallery.module.css';
import { useNavigate } from 'react-router-dom';

function Gallery() {
  const [products, setProducts] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = () => {
    axios.get('http://localhost:5000/api/products', {
      withCredentials: true
    }).then(res => {
      setProducts(res.data);
    });
  };

  // Lọc sản phẩm theo từ khóa
  const filteredProducts = products.filter(product => {
    const name = product.name ? product.name.toLowerCase() : '';
    const id = product.id ? product.id.toString().toLowerCase() : '';
    return name.includes(searchTerm.toLowerCase()) || id.includes(searchTerm.toLowerCase());
  });

  return (
    <div className={styles.galleryContainer}>
      <h2>Sản phẩm</h2>
      <input
        type="text"
        placeholder="Tìm kiếm sản phẩm..."
        value={searchTerm}
        onChange={e => setSearchTerm(e.target.value)}
        style={{ margin: '10px 0', padding: '6px 12px', borderRadius: 6, border: '1px solid #ccc', width: 250 }}
      />
      <ul style={{ listStyle: 'none', padding: 0 }}>
        {filteredProducts.map(product => (
          <li
            key={product.id}
            style={{ cursor: 'pointer', padding: '10px 0', borderBottom: '1px solid #eee', fontSize: 18 }}
            onClick={() => navigate(`/gallery/${product.id}`)}
          >
            {product.name}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Gallery;
