import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Login from './Login';
import Upload from './Upload';
import Gallery from './Gallery';

function App() {
  const [token, setToken] = useState(localStorage.getItem('token'));
  const [role, setRole] = useState(localStorage.getItem('role'));

  const handleLogin = (token, role) => {
    setToken(token);
    setRole(role);
    localStorage.setItem('token', token);
    localStorage.setItem('role', role);
  };
  const handleLogout = () => {
    setToken(null);
    setRole(null);
    localStorage.removeItem('token');
    localStorage.removeItem('role');
  };

  return (
    <Router>
      <div>
        {token && <button onClick={handleLogout}>Đăng xuất</button>}
        <Routes>
          <Route path="/login" element={<Login onLogin={handleLogin} />} />
          <Route path="/upload" element={token && role === 'marketing' ? <Upload token={token} /> : <Navigate to="/login" />} />
          <Route path="/gallery" element={token ? <Gallery token={token} /> : <Navigate to="/login" />} />
          <Route path="*" element={<Navigate to={token ? '/gallery' : '/login'} />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
